import React from 'react';
import { useNavigate } from 'react-router-dom';
import './HomePage.css';
import evacuLogo from './evacu_logo.jpg'; // Adjust the path to match the location of the image

const HomePage = () => {
  const navigate = useNavigate();

  const handleHostLogin = () => {
    navigate('/login');
  };

  const handleHostSignup = () => {
    navigate('/signup');
  };

  const handleGuestLogin = () => {
    navigate('/login');
  };

  const handleGuestSignup = () => {
    navigate('/signup');
  };

  return (
    <div className="home-container">
      <img src={evacuLogo} alt="Evacu Logo" className="logo" />
      <h1 className="home-heading">Welcome to EvacU</h1>
      <p className="home-subheading">Connecting those in need with those who can help</p>
      <div className="options-container">
        <div className="option-card">
          <h2>Host</h2>
          <p>Provide a safe place for those in need.</p>
          <div className="nested-buttons">
            <button className="nested-button" onClick={handleHostLogin}>Login</button>
            <button className="nested-button" onClick={handleHostSignup}>Sign Up</button>
          </div>
        </div>
        <div className="option-card">
          <h2>Guest</h2>
          <p>Find a safe place to stay during a crisis.</p>
          <div className="nested-buttons">
            <button className="nested-button" onClick={handleGuestLogin}>Login</button>
            <button className="nested-button" onClick={handleGuestSignup}>Sign Up</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default HomePage;
