import React from 'react';
import { useNavigate } from 'react-router-dom';
import './HomePage.css';

const HomePage = () => {
  const navigate = useNavigate();

  const handleHostClick = () => {
    navigate('/host');
  };

  const handleGuestClick = () => {
    navigate('/guest');
  };

  return (
    <div className="home-container">
      <h1 className="home-heading">Welcome to EvacU by SHLINK inc.
      </h1>
      <img src={"evacu_logo.jpg"} style={{ width: '50px', height: '50px', marginRight: '10px' }}/>
      <div className="options-container">
        <div className="option-card" onClick={handleHostClick}>
          <h2>Host</h2>
          <p>Provide a safe place for those in need.</p>
        </div>
        <div className="option-card" onClick={handleGuestClick}>
          <h2>Guest</h2>
          <p>Find a safe place to stay during a crisis.</p>
        </div>
      </div>
    </div>
  );
};

export default HomePage;
